import React, { Component } from 'react'

export class TodoItem extends Component {
    getStyle = () => {
        return {
            border: '1px #ccc solid',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none',
            backgroundColor: this.props.todo.completed ? 'green' : '#f4f4f4'
        };
    }

    render() {
    return (
      <div style = {this.getStyle()}>
        <p>
            <input type="checkbox" onClick={this.props.marcarCheckBox.bind(this, this.props.todo.id)}/> {' '}
            <spam>{this.props.todo.title}</spam>
            <button style={{float: 'right'}} type="Submit" value="delete">Deletar</button>
        </p>
      </div>
    )
  }
}

export default TodoItem
