import React, { Component } from 'react'
import { ListaTodo } from './ListaTodo';

export class App extends Component {
    state = {
        todos: [
            {
                id: '1',
                title: 'Capacitação PET',
                completed: false
            },
            {
                id: '2',
                title: 'Comer Bolo',
                completed: false
            },
            {
                id: '3',
                title: 'Tomar refri',
                completed: false
            }
        ]
    }

    marcarCheckBox = (id) => {
        this.state.todos.map((todo) => {
            if(todo.id === id){
                todo.completed = !todo.completed;
            }
            return todo;
        })
    }


    render() {
    return (   
        <div>
          <ListaTodo todos={this.state.todos} marcarCheckBox={this.marcarCheckBox}/>
        </div>
    )
  }
}

export default App
